    public static ArrayList<Game> getListData(){
        Game game = null;
        ArrayList<Game> list = new ArrayList<>();
        for (String[] aData : data) {
            game = new Game();
            game.setName(aData[0]);
            game.setGenre(aData[1]);
            game.setPhoto(aData[2]);
            game.setPrice(aData[3]);
            game.setPlatform(aData[4]);
            game.setDeveloper(aData[5]);
            game.setPublisher(aData[6]);
            game.setRelease(aData[7]);
            game.setDetail(aData[8]);

            list.add(game);
        }

        return list;
    }
